{
  "schema_version": "sections_v1",
  "chapters": [
    {
      "chapter_id": 1,
      "sections": [
        {
          "section_id": 1,
          "title": "The Exit Interview",
          "intent": "Establish Artie's mundane life and sudden, absurd death.",
          "section_role": "setup",
          "target_scene_count": 2,
          "end_condition": "Artie locks in his 'Crit or Quit' build with 1 HP and 100% Critical Hit rate."
        },
        {
          "section_id": 2,
          "title": "The First Spawn",
          "intent": "Show the immediate danger of a 1 HP build in a hostile tutorial zone.",
          "section_role": "escalation",
          "target_scene_count": 3,
          "end_condition": "Artie is forced to run for his life as the tutorial zone aggros on him."
        },
        {
          "section_id": 3,
          "title": "The Safe Zone?",
          "intent": "Reach the first hub and establish the world's culture and primary rivalry.",
          "section_role": "reveal",
          "target_scene_count": 3,
          "end_condition": "Artie makes his first rival in Vane and barely escapes a forced duel."
        }
      ]
    },
    {
      "chapter_id": 2,
      "sections": [
        {
          "section_id": 1,
          "title": "Shopping for Scraps",
          "intent": "Equip Artie with the bare minimum to survive a dungeon through exploits.",
          "section_role": "setup",
          "target_scene_count": 3,
          "end_condition": "Artie acquires the 'Cloak of the Cowardly Lion' and a cursed beacon item."
        },
        {
          "section_id": 2,
          "title": "The Crypt of Bad Math",
          "intent": "Test the new gear and the Crit-build in a controlled environment.",
          "section_role": "action",
          "target_scene_count": 3,
          "end_condition": "Artie discovers he can farm resources via System Overload effects."
        },
        {
          "section_id": 3,
          "title": "The Rival's Shadow",
          "intent": "Reintroduce Vane as a persistent threat and trigger the boss encounter.",
          "section_role": "escalation",
          "target_scene_count": 3,
          "end_condition": "The Bone Golem emerges, placing Artie at a massive mechanical disadvantage."
        }
      ]
    },
    {
      "chapter_id": 3,
      "sections": [
        {
          "section_id": 1,
          "title": "The Dance of Death",
          "intent": "A high-octane fight where Artie must dodge every single attack.",
          "section_role": "action",
          "target_scene_count": 3,
          "end_condition": "Artie collapses the floor, trapping the Golem in a physics glitch."
        },
        {
          "section_id": 2,
          "title": "The Final Blow",
          "intent": "Resolve the boss fight with a comedic but effective exploit.",
          "section_role": "climax",
          "target_scene_count": 3,
          "end_condition": "Artie hits Level 10 and the System begins monitoring him closely."
        },
        {
          "section_id": 3,
          "title": "Looting the Teacups",
          "intent": "Examine the rewards and the consequences of the fight.",
          "section_role": "aftermath",
          "target_scene_count": 4,
          "end_condition": "Artie uses his new speed to outrun Vane's party toward the capital."
        }
      ]
    },
    {
      "chapter_id": 4,
      "sections": [
        {
          "section_id": 1,
          "title": "The City of Gold and Glitches",
          "intent": "Establish the capital city and the religious weight of 'Crits'.",
          "section_role": "setup",
          "target_scene_count": 2,
          "end_condition": "Artie realizes his 1 HP build is permanent and illegal to fix."
        },
        {
          "section_id": 2,
          "title": "The Underground Market",
          "intent": "Artie looks for illegal mods to help him survive.",
          "section_role": "investigation",
          "target_scene_count": 3,
          "end_condition": "Artie escapes a Sentinel raid with a Lag-Switch potion and a binary speech curse."
        },
        {
          "section_id": 3,
          "title": "The Arena Invitation",
          "intent": "Set up the tournament climax and the high financial stakes.",
          "section_role": "escalation",
          "target_scene_count": 3,
          "end_condition": "Vane prepares to 'delete' Artie in the upcoming tournament."
        }
      ]
    },
    {
      "chapter_id": 5,
      "sections": [
        {
          "section_id": 1,
          "title": "The Round of 32",
          "intent": "Show Artie's growth and tactical use of his crits in the arena.",
          "section_role": "action",
          "target_scene_count": 3,
          "end_condition": "Artie moves to the quarter-finals despite mid-match System nerfs."
        },
        {
          "section_id": 2,
          "title": "The Quarter-Finals: The Mirror Match",
          "intent": "Artie faces a character with high evasion, forcing a change in strategy.",
          "section_role": "escalation",
          "target_scene_count": 4,
          "end_condition": "The System applies a 'Random Debuff' to Artie for his next match."
        },
        {
          "section_id": 3,
          "title": "The Semi-Finals: The Debuff",
          "intent": "Artie must overcome a mechanical handicap (Inverted Controls).",
          "section_role": "trial",
          "target_scene_count": 4,
          "end_condition": "Artie reaches the finals against a rigged Vane."
        }
      ]
    },
    {
      "chapter_id": 6,
      "sections": [
        {
          "section_id": 1,
          "title": "Vane's Ultimate Build",
          "intent": "Show the power of a 'Whale' player vs. a 'Glitch' player.",
          "section_role": "setup",
          "target_scene_count": 4,
          "end_condition": "Artie targets the magic aura around Vane's armor to bypass the Reflect buff."
        },
        {
          "section_id": 2,
          "title": "Breaking the Game",
          "intent": "The moment of peak absurdity where the world begins to dissolve.",
          "section_role": "climax",
          "target_scene_count": 6,
          "end_condition": "Artie and Fizz escape deletion as the world enters 'Safe Mode'."
        },
        {
          "section_id": 3,
          "title": "The Aftermath of the Crash",
          "intent": "Show the immediate consequences of the reality crash.",
          "section_role": "aftermath",
          "target_scene_count": 2,
          "end_condition": "Artie realizes his HP is now 'NaN' and he has become a virus."
        }
      ]
    },
    {
      "chapter_id": 7,
      "sections": [
        {
          "section_id": 1,
          "title": "The New Normal",
          "intent": "Establish the new status quo for Artie and Fizz in the Null-Zone.",
          "section_role": "setup",
          "target_scene_count": 3,
          "end_condition": "Artie realizes his immortality is as glitchy and annoying as his 1 HP build."
        },
        {
          "section_id": 2,
          "title": "The Resistance",
          "intent": "Introduce the Beta-Tester faction and the long-term goal.",
          "section_role": "reveal",
          "target_scene_count": 3,
          "end_condition": "The series quest to 'Delete the Admin' is officially locked in."
        },
        {
          "section_id": 3,
          "title": "The First Step",
          "intent": "End on a high note of action and anticipation for the sequel.",
          "section_role": "transition",
          "target_scene_count": 2,
          "end_condition": "Artie steps into a portal to the Glitch-Woods, ending Book 1."
        }
      ]
    }
  ]
}

