{
  "schema_version": "spine_v1",
  "chapters": [
    {
      "chapter_id": 1,
      "title": "Welcome to the RNG-Hellscape",
      "goal": "Introduce Artie and his broken 'Crit-Only' status in a high-stakes environment.",
      "chapter_role": "hook",
      "stakes_shift": "Artie shifts from a mundane office drone to a level 1 glitch in a lethal game world.",
      "bridge": {
        "from_prev": "N/A",
        "to_next": "Artie must find a way to survive his first night with zero defensive stats."
      },
      "pacing": {
        "intensity": 4,
        "tempo": "rush",
        "expected_scene_count": 8
      }
    },
    {
      "chapter_id": 2,
      "title": "The Glass Cannon's Guide to Not Dying",
      "goal": "Artie needs gear that doesn't rely on him being hit, as he can't take a single blow.",
      "chapter_role": "setup",
      "stakes_shift": "Survival shifts from pure luck to tactical exploitation of the System's bugs.",
      "bridge": {
        "from_prev": "Artie is a marked man in Oakhaven.",
        "to_next": "Artie heads to the Low-Level Crypts to farm 'Evasion' gear."
      },
      "pacing": {
        "intensity": 3,
        "tempo": "steady",
        "expected_scene_count": 9
      }
    },
    {
      "chapter_id": 3,
      "title": "Math-Boss vs. The Glitch",
      "goal": "Survive the Floor Guardian and secure the first major loot drop.",
      "chapter_role": "pressure",
      "stakes_shift": "Artie moves from a lucky survivor to a recognized 'Anomaly' with significant power potential.",
      "bridge": {
        "from_prev": "The Bone Golem is ready to smash Artie.",
        "to_next": "Artie leaves the crypts with a legendary-tier item that he can't actually use yet."
      },
      "pacing": {
        "intensity": 5,
        "tempo": "steady",
        "expected_scene_count": 10
      }
    },
    {
      "chapter_id": 4,
      "title": "The Capital of Crits",
      "goal": "Artie must hide in plain sight while trying to find a way to stabilize his build.",
      "chapter_role": "investigation",
      "stakes_shift": "The stakes move from physical survival to social navigation and avoiding the 'System Police'.",
      "bridge": {
        "from_prev": "Artie is a fugitive from Vane's ego.",
        "to_next": "Artie enters the Grand Arena for a 'Low-Stakes' tournament."
      },
      "pacing": {
        "intensity": 2,
        "tempo": "slow_burn",
        "expected_scene_count": 8
      }
    },
    {
      "chapter_id": 5,
      "title": "The Tournament of Tiers",
      "goal": "Artie must fight through the brackets without taking a single point of damage.",
      "chapter_role": "trial",
      "stakes_shift": "Public exposure turns Artie from a hidden glitch into a populist hero and a high-priority System threat.",
      "bridge": {
        "from_prev": "Artie is the underdog in the arena.",
        "to_next": "Artie reaches the finals against Vane."
      },
      "pacing": {
        "intensity": 4,
        "tempo": "steady",
        "expected_scene_count": 11
      }
    },
    {
      "chapter_id": 6,
      "title": "The Final Roll",
      "goal": "Defeat Vane and survive the System's attempt to delete Artie.",
      "chapter_role": "climax",
      "stakes_shift": "The conflict escalates from a personal duel to a reality-breaking crash of the entire game world.",
      "bridge": {
        "from_prev": "Artie enters the ring for the final match.",
        "to_next": "The System crashes, and Artie is left in the wreckage."
      },
      "pacing": {
        "intensity": 5,
        "tempo": "rush",
        "expected_scene_count": 12
      }
    },
    {
      "chapter_id": 7,
      "title": "Rebooting for the Sequel",
      "goal": "Set up the long-term quest to fix (or further break) the world.",
      "chapter_role": "aftermath",
      "stakes_shift": "Artie transitions from a player trying to survive to a virus-entity leading a resistance against the System.",
      "bridge": {
        "from_prev": "Artie is in the Null-Zone.",
        "to_next": "Artie begins the quest for the Source Code in Book 2."
      },
      "pacing": {
        "intensity": 2,
        "tempo": "slow_burn",
        "expected_scene_count": 8
      }
    }
  ]
}

