{
  "schema_version": "sections_v1",
  "chapters": [
    {
      "chapter_id": 1,
      "sections": [
        {
          "section_id": 1,
          "title": "The Exit Interview",
          "intent": "Establish Artie's mundane life and his transition into the broken 'Crit-Only' mechanics.",
          "section_role": "setup",
          "target_scene_count": 2,
          "end_condition": "Artie is locked into a 'Crit or Quit' build with 1 HP and 100% Critical Hit rate."
        },
        {
          "section_id": 2,
          "title": "The First Spawn",
          "intent": "Demonstrate the lethal fragility of the 1 HP build in a hostile environment.",
          "section_role": "development",
          "target_scene_count": 3,
          "end_condition": "Artie is forced to flee the tutorial zone as it aggressively aggros on his anomaly status."
        },
        {
          "section_id": 3,
          "title": "The Safe Zone?",
          "intent": "Introduce the hub world, its culture of RNG worship, and Artie's first major antagonist.",
          "section_role": "transition",
          "target_scene_count": 3,
          "end_condition": "Artie makes a high-level rival in Vane and barely escapes a forced duel."
        }
      ]
    },
    {
      "chapter_id": 2,
      "sections": [
        {
          "section_id": 1,
          "title": "Shopping for Scraps",
          "intent": "Equip Artie with the bare minimum gear required to survive a dungeon without taking damage.",
          "section_role": "setup",
          "target_scene_count": 3,
          "end_condition": "Artie acquires the 'Cloak of the Cowardly Lion' and looks like a neon yellow cat."
        },
        {
          "section_id": 2,
          "title": "The Crypt of Bad Math",
          "intent": "Test the Crit-build's resource farming potential in a controlled dungeon crawl.",
          "section_role": "development",
          "target_scene_count": 3,
          "end_condition": "Artie discovers he can turn enemies into loot-filled pinatas via System Overload."
        },
        {
          "section_id": 3,
          "title": "The Rival's Shadow",
          "intent": "Escalate the tension by showing Vane's power and forcing Artie into a boss encounter.",
          "section_role": "escalation",
          "target_scene_count": 3,
          "end_condition": "The Bone Golem emerges, initiating a boss battle where Artie is at a massive disadvantage."
        }
      ]
    },
    {
      "chapter_id": 3,
      "sections": [
        {
          "section_id": 1,
          "title": "The Dance of Death",
          "intent": "A high-stakes combat sequence where Artie must exploit physics to survive a 'Crit-Resist' boss.",
          "section_role": "action",
          "target_scene_count": 3,
          "end_condition": "The floor collapses, trapping the Bone Golem in a hole."
        },
        {
          "section_id": 2,
          "title": "The Final Blow",
          "intent": "Resolve the boss fight through a comedic System exploit and secure massive XP.",
          "section_role": "resolution",
          "target_scene_count": 3,
          "end_condition": "Artie hits Level 10 instantly after the System panics during XP recalculation."
        },
        {
          "section_id": 3,
          "title": "Looting the Teacups",
          "intent": "Establish deeper world-building regarding 'First Glitches' and escape Vane's pursuit.",
          "section_role": "aftermath",
          "target_scene_count": 4,
          "end_condition": "Artie uses his new speed to outrun Vane's party toward the capital city."
        }
      ]
    },
    {
      "chapter_id": 4,
      "sections": [
        {
          "section_id": 1,
          "title": "The City of Gold and Glitches",
          "intent": "Explore the religious reverence for RNG in the capital and the illegality of stat-wiping.",
          "section_role": "setup",
          "target_scene_count": 2,
          "end_condition": "Artie accepts that he is stuck with his 1 HP glass cannon build forever."
        },
        {
          "section_id": 2,
          "title": "The Underground Market",
          "intent": "Introduce the 'Dev-Haters' faction and provide Artie with illegal survival tools.",
          "section_role": "investigation",
          "target_scene_count": 3,
          "end_condition": "Artie escapes a sentinel raid but is left speaking in binary for an hour."
        },
        {
          "section_id": 3,
          "title": "The Arena Invitation",
          "intent": "Force Artie into the public spotlight through financial desperation and a high-stakes tournament.",
          "section_role": "transition",
          "target_scene_count": 3,
          "end_condition": "Vane sees Artie's name on the tournament roster, setting the stage for the final duel."
        }
      ]
    },
    {
      "chapter_id": 5,
      "sections": [
        {
          "section_id": 1,
          "title": "The Round of 32",
          "intent": "Showcase Artie's growing tactical creativity and the System's attempt to nerf him mid-match.",
          "section_role": "trial",
          "target_scene_count": 3,
          "end_condition": "Artie moves to the quarter-finals after vacuum-critting his opponent out of the ring."
        },
        {
          "section_id": 2,
          "title": "The Mirror Match",
          "intent": "Artie faces a high-evasion rogue, forcing him to use unconventional environmental exploits.",
          "section_role": "trial",
          "target_scene_count": 4,
          "end_condition": "Artie wins but receives a System warning for 'Unsportsmanlike Exploitation'."
        },
        {
          "section_id": 3,
          "title": "The Semi-Finals",
          "intent": "Artie overcomes a mechanical handicap ('Inverted Controls') to reach the finals.",
          "section_role": "escalation",
          "target_scene_count": 4,
          "end_condition": "Vane prepares to use a 'System Override' scroll to rig the final fight."
        }
      ]
    },
    {
      "chapter_id": 6,
      "sections": [
        {
          "section_id": 1,
          "title": "Vane's Ultimate Build",
          "intent": "A desperate struggle where Vane's 'Whale' gear and System hacks strip Artie of his defenses.",
          "section_role": "action",
          "target_scene_count": 4,
          "end_condition": "Artie realizes he must target the magic aura of Vane's 'Reflect' buff directly."
        },
        {
          "section_id": 2,
          "title": "Breaking the Game",
          "intent": "The peak climax where Artie's 'Meta-Crit' crashes the game and he faces the System Administrator.",
          "section_role": "climax",
          "target_scene_count": 6,
          "end_condition": "Artie and Fizz teleport to a 'Null-Zone' as the world enters Safe Mode."
        },
        {
          "section_id": 3,
          "title": "The Aftermath of the Crash",
          "intent": "Establish the immediate consequences of the reality-break and Artie's new status.",
          "section_role": "aftermath",
          "target_scene_count": 2,
          "end_condition": "Artie realizes his HP is no longer 1, but 'NaN' (Not a Number)."
        }
      ]
    },
    {
      "chapter_id": 7,
      "sections": [
        {
          "section_id": 1,
          "title": "The New Normal",
          "intent": "Show Artie adapting to life as a 'virus' entity within the broken game world.",
          "section_role": "setup",
          "target_scene_count": 3,
          "end_condition": "Artie realizes his new immortality is just as annoying as his previous fragility."
        },
        {
          "section_id": 2,
          "title": "The Resistance",
          "intent": "Introduce the Beta-Tester faction and set up the series-long pursuit of the Admin.",
          "section_role": "development",
          "target_scene_count": 3,
          "end_condition": "Artie accepts the legendary quest to 'Delete the Admin'."
        },
        {
          "section_id": 3,
          "title": "The First Step",
          "intent": "Conclude the book with a transition toward the next adventure and a comedic final beat.",
          "section_role": "transition",
          "target_scene_count": 2,
          "end_condition": "Artie steps into a portal to the Glitch-Woods, hoping for better drop rates."
        }
      ]
    }
  ]
}

