{
  "schema_version": "spine_v1",
  "chapters": [
    {
      "chapter_id": 1,
      "title": "Welcome to the RNG-Hellscape",
      "goal": "Introduce Artie and his broken 'Crit-Only' status in a high-stakes environment.",
      "chapter_role": "hook",
      "stakes_shift": "Artie shifts from a mundane office drone to a Level 1 glitch in a lethal game world.",
      "bridge": {
        "from_prev": "N/A",
        "to_next": "Artie must find a way to survive his first night with zero defensive stats."
      },
      "pacing": {
        "intensity": 4,
        "tempo": "rush",
        "expected_scene_count": 8
      }
    },
    {
      "chapter_id": 2,
      "title": "The Glass Cannon's Guide to Not Dying",
      "goal": "Artie needs gear that doesn't rely on him being hit, as he can't take a single blow.",
      "chapter_role": "setup",
      "stakes_shift": "Survival shifts from raw luck to the tactical exploitation of the System's bugs.",
      "bridge": {
        "from_prev": "Artie is a marked man in Oakhaven with no defense.",
        "to_next": "Artie triggers a Floor Guardian to test his exploit, risking a Game Over for massive XP."
      },
      "pacing": {
        "intensity": 3,
        "tempo": "steady",
        "expected_scene_count": 9
      }
    },
    {
      "chapter_id": 3,
      "title": "Math-Boss vs. The Glitch",
      "goal": "Survive the Floor Guardian and secure the first major loot drop.",
      "chapter_role": "pressure",
      "stakes_shift": "Artie moves from a lucky survivor to a high-level Anomaly with a target on his back.",
      "bridge": {
        "from_prev": "The Bone Golem is ready to smash Artie.",
        "to_next": "Artie flees Vane's wrath, seeking refuge in the capital to stabilize his broken stats."
      },
      "pacing": {
        "intensity": 5,
        "tempo": "steady",
        "expected_scene_count": 10
      }
    },
    {
      "chapter_id": 4,
      "title": "The Capital of Crits",
      "goal": "Artie must hide in plain sight while trying to find a way to stabilize his build.",
      "chapter_role": "investigation",
      "stakes_shift": "Physical survival becomes social navigation and avoiding 'System Police' deletion.",
      "bridge": {
        "from_prev": "Artie is a fugitive from Vane's ego.",
        "to_next": "Broke and exposed, Artie enters the Grand Arena to win the ultimate defensive item."
      },
      "pacing": {
        "intensity": 2,
        "tempo": "slow_burn",
        "expected_scene_count": 8
      }
    },
    {
      "chapter_id": 5,
      "title": "The Tournament of Tiers",
      "goal": "Artie must fight through the brackets without taking a single point of damage.",
      "chapter_role": "trial",
      "stakes_shift": "Artie's local fame turns into System-wide notoriety, triggering aggressive 'balancing' measures.",
      "bridge": {
        "from_prev": "Artie is the underdog in the arena.",
        "to_next": "The tournament finals become a rigged trap that forces Artie to break the game's math."
      },
      "pacing": {
        "intensity": 4,
        "tempo": "steady",
        "expected_scene_count": 11
      }
    },
    {
      "chapter_id": 6,
      "title": "The Final Roll",
      "goal": "Defeat Vane and survive the System's attempt to delete Artie.",
      "chapter_role": "climax",
      "stakes_shift": "Personal rivalry escalates to the potential collapse of the game world's source code.",
      "bridge": {
        "from_prev": "Artie enters the ring for the final match.",
        "to_next": "The world enters Safe Mode after the crash, leaving Artie to navigate the wireframe remains."
      },
      "pacing": {
        "intensity": 5,
        "tempo": "rush",
        "expected_scene_count": 12
      }
    },
    {
      "chapter_id": 7,
      "title": "Rebooting for the Sequel",
      "goal": "Set up the long-term quest to fix (or further break) the world.",
      "chapter_role": "aftermath",
      "stakes_shift": "Artie shifts from a player trying to survive to the most wanted virus in the multiverse.",
      "bridge": {
        "from_prev": "Artie is in the Null-Zone.",
        "to_next": "Artie begins the quest for the Source Code in the Glitch-Woods."
      },
      "pacing": {
        "intensity": 2,
        "tempo": "slow_burn",
        "expected_scene_count": 8
      }
    }
  ]
}

