{
  "schema_version": "sections_v1",
  "chapters": [
    {
      "chapter_id": 1,
      "sections": [
        {
          "section_id": 1,
          "title": "Welcome to the Grind",
          "intent": "Introduce the protagonist and the lethal nature of the forced isekai tutorial.",
          "section_role": "setup",
          "target_scene_count": 3,
          "end_condition": "Protagonist enters the active tutorial combat zone."
        },
        {
          "section_id": 2,
          "title": "The Infinite Death Loop",
          "intent": "Establish the 'Instant-Death' mechanic and the protagonist's discovery of a physics exploit.",
          "section_role": "rising_action",
          "target_scene_count": 4,
          "end_condition": "Protagonist successfully triggers the physics glitch for the first time."
        },
        {
          "section_id": 3,
          "title": "Breaking the Box",
          "intent": "The protagonist uses the glitch to bypass the tutorial boss and loot restricted items.",
          "section_role": "climax",
          "target_scene_count": 3,
          "end_condition": "Protagonist exits the tutorial zone with illegal starter gear."
        }
      ]
    },
    {
      "chapter_id": 2,
      "sections": [
        {
          "section_id": 1,
          "title": "The Hub and the Mark",
          "intent": "Introduce the world hub and identify a high-value, high-level target.",
          "section_role": "setup",
          "target_scene_count": 3,
          "end_condition": "The high-level merchant caravan is identified as the heist target."
        },
        {
          "section_id": 2,
          "title": "Crit Hit & Run",
          "intent": "Execute the robbery using the protagonist's unique combat exploit.",
          "section_role": "rising_action",
          "target_scene_count": 3,
          "end_condition": "The caravan is successfully looted without triggering standard combat aggro."
        },
        {
          "section_id": 3,
          "title": "Wanted: Level One",
          "intent": "Deal with the immediate consequences of the heist and the resulting bounty.",
          "section_role": "climax",
          "target_scene_count": 2,
          "end_condition": "Protagonist reaches a safe zone with the stolen loot and a bounty on his head."
        }
      ]
    },
    {
      "chapter_id": 3,
      "sections": [
        {
          "section_id": 1,
          "title": "Bypassing the Gatekeepers",
          "intent": "Enter the Vault of Eternal Sighs and use movement exploits to skip initial puzzles.",
          "section_role": "setup",
          "target_scene_count": 3,
          "end_condition": "The first major mechanical puzzle of the Vault is bypassed."
        },
        {
          "section_id": 2,
          "title": "The Speed-run",
          "intent": "Navigate the dangerous interior of the vault at high speed using glitches.",
          "section_role": "rising_action",
          "target_scene_count": 3,
          "end_condition": "Protagonist reaches the final vault chamber ahead of the intended timeline."
        },
        {
          "section_id": 3,
          "title": "Emptying the Vault",
          "intent": "Secure the massive XP gain and the key quest item from the vault core.",
          "section_role": "climax",
          "target_scene_count": 3,
          "end_condition": "The Vault is cleared and the protagonist's level spikes significantly."
        }
      ]
    },
    {
      "chapter_id": 4,
      "sections": [
        {
          "section_id": 1,
          "title": "The Unbeatable Mob",
          "intent": "The protagonist encounters a high-level ambush that forces reliance on his glitch.",
          "section_role": "setup",
          "target_scene_count": 3,
          "end_condition": "The System Hotfix notification appears mid-combat."
        },
        {
          "section_id": 2,
          "title": "Standard Combat Sucks",
          "intent": "The protagonist must survive a fight after his primary exploit is disabled by the patch.",
          "section_role": "rising_action",
          "target_scene_count": 3,
          "end_condition": "Protagonist survives the initial encounter through desperate, legitimate tactics."
        },
        {
          "section_id": 3,
          "title": "Seeking the Source",
          "intent": "Realizing he can't survive without his edge, the protagonist looks for a way to the administrative layer.",
          "section_role": "climax",
          "target_scene_count": 2,
          "end_condition": "Protagonist resolves to find the hidden Debug Room."
        }
      ]
    },
    {
      "chapter_id": 5,
      "sections": [
        {
          "section_id": 1,
          "title": "The Backdoor Entrance",
          "intent": "Find and infiltrate the hidden entrance to the administrative layer.",
          "section_role": "setup",
          "target_scene_count": 3,
          "end_condition": "Protagonist successfully enters the Administrative Layer of the world."
        },
        {
          "section_id": 2,
          "title": "The Dev Zone",
          "intent": "Navigate the surreal environment of the restricted developer zone.",
          "section_role": "rising_action",
          "target_scene_count": 3,
          "end_condition": "Protagonist reaches the central character terminal."
        },
        {
          "section_id": 3,
          "title": "Overriding the System",
          "intent": "Manually reset the character's status and gain system-level awareness.",
          "section_role": "climax",
          "target_scene_count": 3,
          "end_condition": "Protagonist gains 'System-Aware' status and manipulates world variables."
        }
      ]
    },
    {
      "chapter_id": 6,
      "sections": [
        {
          "section_id": 1,
          "title": "The Administrator's Wrath",
          "intent": "Confront the Regional Administrator who is attempting to delete the anomaly.",
          "section_role": "setup",
          "target_scene_count": 3,
          "end_condition": "The final battle begins within the server room."
        },
        {
          "section_id": 2,
          "title": "Stacking the Errors",
          "intent": "Intentionally spawn errors and glitches to destabilize the server instance.",
          "section_role": "rising_action",
          "target_scene_count": 4,
          "end_condition": "Local server stability reaches a critical, failing threshold."
        },
        {
          "section_id": 3,
          "title": "Hard Reboot",
          "intent": "Defeat the Administrator by forcing a crash and surviving the resulting reset.",
          "section_role": "climax",
          "target_scene_count": 3,
          "end_condition": "Regional Administrator is defeated as the instance crashes."
        }
      ]
    },
    {
      "chapter_id": 7,
      "sections": [
        {
          "section_id": 1,
          "title": "Looting the Crash",
          "intent": "Recover the unique 'God-Slayer' loot from the remains of the Administrator.",
          "section_role": "setup",
          "target_scene_count": 2,
          "end_condition": "The God-Slayer loot is claimed and identified."
        },
        {
          "section_id": 2,
          "title": "The Last Boat Out",
          "intent": "Escape the stabilizing region before the System fully re-asserts control.",
          "section_role": "rising_action",
          "target_scene_count": 2,
          "end_condition": "Protagonist successfully departs the current continent."
        },
        {
          "section_id": 3,
          "title": "New Rules, New Bugs",
          "intent": "Arrive at a new land with higher stakes and the title of Admin-Killer.",
          "section_role": "climax",
          "target_scene_count": 2,
          "end_condition": "Protagonist arrives at the new continent with a target on his back."
        }
      ]
    }
  ]
}

