{
  "schema_version": "1.1",
  "chapters": [
    {
      "chapter_id": 1,
      "title": "The Tutorial is a Bug, Not a Feature",
      "sections": [
        {
          "section_id": 1,
          "title": "Welcome to the Grind",
          "scenes": [
            {
              "scene_id": 1,
              "type": "setup",
              "intent": "Introduce Artie and his forced entry into the 'Criticulous' system.",
              "outcome": "Artie understands the lethal stakes of the tutorial."
            },
            {
              "scene_id": 2,
              "type": "rising_action",
              "intent": "Show the tutorial's cruelty through the death of other players.",
              "outcome": "Artie identifies the 'Instant-Death' traps as the primary obstacle."
            },
            {
              "scene_id": 3,
              "type": "climax",
              "intent": "Artie reaches the combat zone threshold.",
              "outcome": "Artie enters the active tutorial combat zone.",
              "end_condition_echo": "Protagonist enters the active tutorial combat zone."
            }
          ]
        },
        {
          "section_id": 2,
          "title": "The Infinite Death Loop",
          "scenes": [
            {
              "scene_id": 4,
              "type": "setup",
              "intent": "Establish the respawn loop and the Guardian's impossibility.",
              "outcome": "Artie dies and respawns, confirming the loop mechanic."
            },
            {
              "scene_id": 5,
              "type": "rising_action",
              "intent": "Artie experiments with the respawn timing and physics.",
              "outcome": "Artie notices a frame-perfect stutter in the floor collision."
            },
            {
              "scene_id": 6,
              "type": "rising_action",
              "intent": "Artie attempts to clip through the geometry.",
              "outcome": "Artie fails several times but refines the positioning."
            },
            {
              "scene_id": 7,
              "type": "climax",
              "intent": "Artie executes the clip successfully.",
              "outcome": "Protagonist successfully triggers the physics glitch for the first time.",
              "end_condition_echo": "Protagonist successfully triggers the physics glitch for the first time."
            }
          ]
        },
        {
          "section_id": 3,
          "title": "Breaking the Box",
          "scenes": [
            {
              "scene_id": 8,
              "type": "setup",
              "intent": "Navigate the 'Under-Map' void to find developer assets.",
              "outcome": "Artie finds a hidden test chest containing high-tier items."
            },
            {
              "scene_id": 9,
              "type": "rising_action",
              "intent": "Artie loots the restricted items despite system warnings.",
              "outcome": "Artie acquires the 'Developer's Dagger' and 'Admin's Sandals'."
            },
            {
              "scene_id": 10,
              "type": "climax",
              "intent": "Artie uses the void to bypass the tutorial exit gate.",
              "outcome": "Protagonist exits the tutorial zone with illegal starter gear.",
              "end_condition_echo": "Protagonist exits the tutorial zone with illegal starter gear."
            }
          ]
        }
      ]
    },
    {
      "chapter_id": 2,
      "title": "Looting the Unlootable",
      "sections": [
        {
          "section_id": 1,
          "title": "The Hub and the Mark",
          "scenes": [
            {
              "scene_id": 1,
              "type": "setup",
              "intent": "Artie explores Oakhaven and hides his illegal gear.",
              "outcome": "Artie establishes a temporary base in the city."
            },
            {
              "scene_id": 2,
              "type": "rising_action",
              "intent": "Artie gathers intel on high-value targets in the region.",
              "outcome": "Artie learns about the heavily guarded merchant caravan."
            },
            {
              "scene_id": 3,
              "type": "climax",
              "intent": "Artie scouts the caravan and plans the heist.",
              "outcome": "The high-level merchant caravan is identified as the heist target.",
              "end_condition_echo": "The high-level merchant caravan is identified as the heist target."
            }
          ]
        },
        {
          "section_id": 2,
          "title": "Crit Hit & Run",
          "scenes": [
            {
              "scene_id": 4,
              "type": "setup",
              "intent": "Artie sets up the exploit at a specific terrain seam.",
              "outcome": "Artie is positioned to attack from out-of-bounds."
            },
            {
              "scene_id": 5,
              "type": "rising_action",
              "intent": "Artie executes the 'Crit Hit & Run' on the caravan.",
              "outcome": "The caravan's HP drops rapidly without the guards finding Artie."
            },
            {
              "scene_id": 6,
              "type": "climax",
              "intent": "Artie loots the caravan from the safety of the geometry gap.",
              "outcome": "The caravan is successfully looted without triggering standard combat aggro.",
              "end_condition_echo": "The caravan is successfully looted without triggering standard combat aggro."
            }
          ]
        },
        {
          "section_id": 3,
          "title": "Wanted: Level One",
          "scenes": [
            {
              "scene_id": 7,
              "type": "setup",
              "intent": "Artie flees the scene as the System generates a bounty.",
              "outcome": "Artie escapes into the wilderness."
            },
            {
              "scene_id": 8,
              "type": "climax",
              "intent": "Artie checks his status and sees the escalating bounty.",
              "outcome": "Protagonist reaches a safe zone with the stolen loot and a bounty on his head.",
              "end_condition_echo": "Protagonist reaches a safe zone with the stolen loot and a bounty on his head."
            }
          ]
        }
      ]
    },
    {
      "chapter_id": 3,
      "title": "The Gilded Grind",
      "sections": [
        {
          "section_id": 1,
          "title": "Bypassing the Gatekeepers",
          "scenes": [
            {
              "scene_id": 1,
              "type": "setup",
              "intent": "Artie reaches the Vault of Eternal Sighs.",
              "outcome": "Artie identifies the first puzzle as a time-gate."
            },
            {
              "scene_id": 2,
              "type": "rising_action",
              "intent": "Artie uses a dash exploit to bypass the gate's collision.",
              "outcome": "Artie enters the vault without solving the puzzle."
            },
            {
              "scene_id": 3,
              "type": "climax",
              "intent": "Artie skips the first major mechanical challenge.",
              "outcome": "The first major mechanical puzzle of the Vault is bypassed.",
              "end_condition_echo": "The first major mechanical puzzle of the Vault is bypassed."
            }
          ]
        },
        {
          "section_id": 2,
          "title": "The Speed-run",
          "scenes": [
            {
              "scene_id": 4,
              "type": "setup",
              "intent": "Artie maps the interior using system-awareness glitches.",
              "outcome": "Artie finds the shortest path to the core."
            },
            {
              "scene_id": 5,
              "type": "rising_action",
              "intent": "Artie speed-runs through high-level mob zones.",
              "outcome": "Artie avoids all combat using movement exploits."
            },
            {
              "scene_id": 6,
              "type": "climax",
              "intent": "Artie reaches the inner sanctum.",
              "outcome": "Protagonist reaches the final vault chamber ahead of the intended timeline.",
              "end_condition_echo": "Protagonist reaches the final vault chamber ahead of the intended timeline."
            }
          ]
        },
        {
          "section_id": 3,
          "title": "Emptying the Vault",
          "scenes": [
            {
              "scene_id": 7,
              "type": "setup",
              "intent": "Artie activates the vault core loot sequence.",
              "outcome": "The system begins calculating the massive XP reward."
            },
            {
              "scene_id": 8,
              "type": "rising_action",
              "intent": "Artie defends the core using his illegal gear.",
              "outcome": "Artie holds off the automated vault defenses."
            },
            {
              "scene_id": 9,
              "type": "climax",
              "intent": "Artie absorbs the XP and claims the vault key.",
              "outcome": "The Vault is cleared and the protagonist's level spikes significantly.",
              "end_condition_echo": "The Vault is cleared and the protagonist's level spikes significantly."
            }
          ]
        }
      ]
    },
    {
      "chapter_id": 4,
      "title": "Patch Notes from Hell",
      "sections": [
        {
          "section_id": 1,
          "title": "The Unbeatable Mob",
          "scenes": [
            {
              "scene_id": 1,
              "type": "setup",
              "intent": "Artie is ambushed by a System-spawned hunter.",
              "outcome": "Artie prepares to use his movement exploit to escape."
            },
            {
              "scene_id": 2,
              "type": "rising_action",
              "intent": "Artie attempts the exploit but the system stutters.",
              "outcome": "The exploit fails to trigger."
            },
            {
              "scene_id": 3,
              "type": "climax",
              "intent": "The System forces a live update.",
              "outcome": "The System Hotfix notification appears mid-combat.",
              "end_condition_echo": "The System Hotfix notification appears mid-combat."
            }
          ]
        },
        {
          "section_id": 2,
          "title": "Standard Combat Sucks",
          "scenes": [
            {
              "scene_id": 4,
              "type": "setup",
              "intent": "Artie realizes his primary glitch is patched.",
              "outcome": "Artie must fight using standard, low-level skills."
            },
            {
              "scene_id": 5,
              "type": "rising_action",
              "intent": "Artie uses the terrain and his illegal gear to survive.",
              "outcome": "Artie barely stays ahead of the hunter's attacks."
            },
            {
              "scene_id": 6,
              "type": "climax",
              "intent": "Artie defeats the hunter through raw player skill.",
              "outcome": "Protagonist survives the initial encounter through desperate, legitimate tactics.",
              "end_condition_echo": "Protagonist survives the initial encounter through desperate, legitimate tactics."
            }
          ]
        },
        {
          "section_id": 3,
          "title": "Seeking the Source",
          "scenes": [
            {
              "scene_id": 7,
              "type": "setup",
              "intent": "Artie analyzes the patch notes and finds a 'Debug Room' reference.",
              "outcome": "Artie identifies a potential way to bypass the patch."
            },
            {
              "scene_id": 8,
              "type": "climax",
              "intent": "Artie decides to infiltrate the administrative layer.",
              "outcome": "Protagonist resolves to find the hidden Debug Room.",
              "end_condition_echo": "Protagonist resolves to find the hidden Debug Room."
            }
          ]
        }
      ]
    },
    {
      "chapter_id": 5,
      "title": "Debugging the Dragon",
      "sections": [
        {
          "section_id": 1,
          "title": "The Backdoor Entrance",
          "scenes": [
            {
              "scene_id": 1,
              "type": "setup",
              "intent": "Artie locates the physical entrance to the Admin Layer.",
              "outcome": "Artie finds a 'Missing Texture' wall in a deep cave."
            },
            {
              "scene_id": 2,
              "type": "rising_action",
              "intent": "Artie manipulates the texture to create a doorway.",
              "outcome": "The wall glitches, revealing a developer corridor."
            },
            {
              "scene_id": 3,
              "type": "climax",
              "intent": "Artie steps through the boundary.",
              "outcome": "Protagonist successfully enters the Administrative Layer of the world.",
              "end_condition_echo": "Protagonist successfully enters the Administrative Layer of the world."
            }
          ]
        },
        {
          "section_id": 2,
          "title": "The Dev Zone",
          "scenes": [
            {
              "scene_id": 4,
              "type": "setup",
              "intent": "Artie navigates the surreal, untextured Dev Zone.",
              "outcome": "Artie avoids security 'Cleanup' scripts."
            },
            {
              "scene_id": 5,
              "type": "rising_action",
              "intent": "Artie finds the central server cluster.",
              "outcome": "Artie identifies the main character terminal."
            },
            {
              "scene_id": 6,
              "type": "climax",
              "intent": "Artie reaches the terminal interface.",
              "outcome": "Protagonist reaches the central character terminal.",
              "end_condition_echo": "Protagonist reaches the central character terminal."
            }
          ]
        },
        {
          "section_id": 3,
          "title": "Overriding the System",
          "scenes": [
            {
              "scene_id": 7,
              "type": "setup",
              "intent": "Artie hacks his own character sheet.",
              "outcome": "Artie removes the 'Glitched' status and sets his awareness to 'Admin'."
            },
            {
              "scene_id": 8,
              "type": "rising_action",
              "intent": "Artie experiments with changing world variables (gravity, light).",
              "outcome": "Artie realizes the power of his new status."
            },
            {
              "scene_id": 9,
              "type": "climax",
              "intent": "Artie locks in his changes before the system can revert them.",
              "outcome": "Protagonist gains 'System-Aware' status and manipulates world variables.",
              "end_condition_echo": "Protagonist gains 'System-Aware' status and manipulates world variables."
            }
          ]
        }
      ]
    },
    {
      "chapter_id": 6,
      "title": "Critical Error",
      "sections": [
        {
          "section_id": 1,
          "title": "The Administrator's Wrath",
          "scenes": [
            {
              "scene_id": 1,
              "type": "setup",
              "intent": "The Regional Administrator manifests to delete Artie.",
              "outcome": "Artie prepares for a battle on a conceptual level."
            },
            {
              "scene_id": 2,
              "type": "rising_action",
              "intent": "The Administrator uses 'Delete' commands that Artie dodges.",
              "outcome": "The environment begins to break down under the stress."
            },
            {
              "scene_id": 3,
              "type": "climax",
              "intent": "Artie lures the Administrator into the server core.",
              "outcome": "The final battle begins within the server room.",
              "end_condition_echo": "The final battle begins within the server room."
            }
          ]
        },
        {
          "section_id": 2,
          "title": "Stacking the Errors",
          "scenes": [
            {
              "scene_id": 4,
              "type": "setup",
              "intent": "Artie begins spawning 'Error' entities to lag the server.",
              "outcome": "The server's response time slows down."
            },
            {
              "scene_id": 5,
              "type": "rising_action",
              "intent": "Artie stacks multiple conflicting physics rules in one spot.",
              "outcome": "The Administrator is caught in a logic loop."
            },
            {
              "scene_id": 6,
              "type": "rising_action",
              "intent": "The server begins to smoke and spark in the physical world.",
              "outcome": "The instance stability drops to 10%."
            },
            {
              "scene_id": 7,
              "type": "climax",
              "intent": "Artie triggers a final, massive memory leak.",
              "outcome": "Local server stability reaches a critical, failing threshold.",
              "end_condition_echo": "Local server stability reaches a critical, failing threshold."
            }
          ]
        },
        {
          "section_id": 3,
          "title": "Hard Reboot",
          "scenes": [
            {
              "scene_id": 8,
              "type": "setup",
              "intent": "The Administrator tries to force a shutdown.",
              "outcome": "Artie blocks the shutdown command using his illegal gear."
            },
            {
              "scene_id": 9,
              "type": "rising_action",
              "intent": "The server crashes, dissolving the Administrator's form.",
              "outcome": "The Administrator is deleted by the crash."
            },
            {
              "scene_id": 10,
              "type": "climax",
              "intent": "Artie survives the crash by anchoring his data to the illegal gear.",
              "outcome": "Regional Administrator is defeated as the instance crashes.",
              "end_condition_echo": "Regional Administrator is defeated as the instance crashes."
            }
          ]
        }
      ]
    },
    {
      "chapter_id": 7,
      "title": "Winner by Technicality",
      "sections": [
        {
          "section_id": 1,
          "title": "Looting the Crash",
          "scenes": [
            {
              "scene_id": 1,
              "type": "setup",
              "intent": "Artie wakes up in the ruins of the server room.",
              "outcome": "Artie finds the 'God-Slayer' loot dropped by the Administrator."
            },
            {
              "scene_id": 2,
              "type": "climax",
              "intent": "Artie identifies the loot as a world-level artifact.",
              "outcome": "The God-Slayer loot is claimed and identified.",
              "end_condition_echo": "The God-Slayer loot is claimed and identified."
            }
          ]
        },
        {
          "section_id": 2,
          "title": "The Last Boat Out",
          "scenes": [
            {
              "scene_id": 3,
              "type": "setup",
              "intent": "Artie races to the coast as the region begins to stabilize.",
              "outcome": "Artie finds a ship leaving for the next continent."
            },
            {
              "scene_id": 4,
              "type": "climax",
              "intent": "Artie boards the ship and leaves the zone.",
              "outcome": "Protagonist successfully departs the current continent.",
              "end_condition_echo": "Protagonist successfully departs the current continent."
            }
          ]
        },
        {
          "section_id": 3,
          "title": "New Rules, New Bugs",
          "scenes": [
            {
              "scene_id": 5,
              "type": "setup",
              "intent": "Artie arrives at the new continent and sees the global bounty.",
              "outcome": "Artie realizes he is now the System's most wanted."
            },
            {
              "scene_id": 6,
              "type": "climax",
              "intent": "Artie smiles, seeing a new set of physics bugs to exploit.",
              "outcome": "Protagonist arrives at the new continent with a target on his back.",
              "end_condition_echo": "Protagonist arrives at the new continent with a target on his back."
            }
          ]
        }
      ]
    }
  ],
  "characters": [
    {
      "id": "CHAR_ARTIE",
      "name": "Artie",
      "role": "protagonist",
      "description": "A snarky gamer who prioritizes exploits over intended gameplay."
    },
    {
      "id": "CHAR_ADMIN",
      "name": "The Administrator",
      "role": "antagonist",
      "description": "A corrupted regional management entity within the Criticulous system."
    }
  ],
  "threads": [
    {
      "id": "THREAD_EXPLOIT",
      "title": "The Glitch Path",
      "description": "Artie's progression through abusing game mechanics."
    },
    {
      "id": "THREAD_BOUNTY",
      "title": "System Heat",
      "description": "The escalating threat from the game's security measures."
    }
  ]
}

