    ]
  },
  "appearance_art": {
    "base_prompt": "A pale human male in his late-20s with a slight build and average height, featuring unruly short mousy brown hair and hazel eyes.",
    "current_prompt": "A pale human male in his late-20s with a slight build and unruly short mousy brown hair, featuring a small scar on his right knuckle and a vibrant neon yellow glow emanating from his torso.",
    "negative_prompt": "muscular, athletic, tall, beard, long hair, armor, fantasy robes, dark skin, jewelry, heavy clothing.",
