      "containers": [
        {"container": "inner_pocket", "owner": "CHAR_MAYOR", "location": "chest", "contents": ["Seal of Office"]},
        {"container": "hand_left", "owner": "CHAR_MAYOR", "location": "arm_left", "contents": ["Bag of Gold"]}
      ],
      "invariants": [
        "inventory: CHAR_MAYOR -> Seal of Office (status=stowed, container=inner_pocket)",
        "inventory: CHAR_MAYOR -> Bag of Gold (status=carried, container=hand_left)",
        "container: inner_pocket (owner=CHAR_MAYOR, contents=[Seal of Office])",
        "container: hand_left (owner=CHAR_MAYOR, contents=[Bag of Gold])"
      ]
    },
    {
      "character_id": "CHAR_BOSS",
      "name": "The System Administrator",
      "pronouns": "it/its",
      "role": "overarching villain",
      "persona": {
        "core_traits": ["Cold", "Logical", "Detached", "Ruthless"],
        "motivations": ["Optimization", "Purging anomalies", "Maintaining System integrity"],
        "fears": ["Total System crash", "Unpredictability", "User rebellion"],
        "values": ["Order", "Logic", "Efficiency"],
        "voice_notes": ["Monotone", "Uses plural 'We'", "Highly technical", "Emotionless"],
        "arc_hint": "The ultimate arbiter of the world's rules that Artie must eventually outsmart."
      },
      "inventory": [
