      "invariants": [
        "inventory: CHAR_FIZZ -> guide_book (status=stowed, container=tiny_satchel)",
        "container: tiny_satchel (owner=CHAR_FIZZ, contents=[guide_book])"
      ]
    },
    {
      "character_id": "CHAR_VANE",
      "name": "Vane",
      "pronouns": "he/him",
      "role": "antagonist",
      "persona": {
        "core_traits": ["arrogant", "entitled", "aggressive", "status-obsessed"],
        "motivations": ["dominating Oakhaven", "gaining the 'Chosen One' title", "crushing Artie"],
        "fears": ["irrelevance", "looking weak", "actual hard work"],
        "values": ["might makes right", "rarity of gear", "social standing"],
        "voice_notes": ["condescending", "boastful", "prone to monologuing"],
        "arc_hint": "A 'perfect' hero archetype who becomes increasingly unhinged as Artie's chaos ruins his scripts."
      },
      "character_continuity_system_state": {
        "stats": {
          "level": 5,
          "hp": {"current": 150, "max": 150},
          "str": 16,
          "dex": 10
