      },
      "inventory": [
        {"item": "iron_sword", "status": "equipped", "container": "belt_sheath"}
      ],
      "containers": [
        {"container": "belt_sheath", "owner": "CHAR_VANE", "location": "waist", "contents": ["iron_sword"]}
