      "character_id": "CHAR_MAYOR",
      "name": "Mayor of Oakhaven",
      "pronouns": "he/him",
      "role": "npc",
      "persona": {
        "core_traits": ["fretful", "bureaucratic", "traditional", "exhausted"],
        "motivations": ["keeping the peace", "meeting tax quotas", "retiring"],
        "fears": ["monster raids", "the System Administrator's audits", "paperwork"],
        "values": ["order", "precedent", "the town's safety"],
        "voice_notes": ["formal but shaky", "sighs frequently", "repeats canned quest dialogue"],
        "arc_hint": "Goes from a scripted quest-giver to a man realizing his reality is a simulation."
      },
      "character_continuity_system_state": {
        "stats": {
          "hp": {"current": 50, "max": 50}
        },
        "skills": {
          "taxation": 8,
          "speechify": 4
        },
        "titles": [
          {"name": "Stressed Bureaucrat", "source": "occupation", "active": true}
        ]
      },
      "inventory": [
        {"item": "town_seal", "status": "stowed", "container": "vest_pocket"}
      ],
      "containers": [
        {"container": "vest_pocket", "owner": "CHAR_MAYOR", "location": "chest", "contents": ["town_seal"]}
      ],
      "invariants": [
        "inventory: CHAR_MAYOR -> town_seal (status=stowed, container=vest_pocket)",
        "container: vest_pocket (owner=CHAR_MAYOR, contents=[town_seal])"
      ]
    },
    {
      "character_id": "CHAR_BOSS",
      "name": "The System Administrator",
      "pronouns": "it/its",
      "role": "overarching villain",
      "persona": {
        "core_traits": ["detached", "absolute", "logical", "cruel"],
        "motivations": ["maintaining system balance", "purging anomalies", "optimizing efficiency"],
        "fears": ["unfixable bugs", "total system crash"],
        "values": ["logic", "structure", "the Patch Notes"],
        "voice_notes": ["monotone", "multi-layered resonance", "speaks in code snippets"],
        "arc_hint": "The ultimate arbiter of the world's rules who finds Artie's existence to be an intolerable error."
      },
      "character_continuity_system_state": {
        "stats": {
          "level": 99,
          "hp": {"current": 9999, "max": 9999}
        },
        "skills": {
          "ban_hammer": 10,
          "patch_note": 10,
          "reality_override": 10
        },
        "titles": [
          {"name": "The Architect", "source": "origin", "active": true}
        ]
      },
      "inventory": [
        {"item": "master_key", "status": "carried", "container": "hand_right"}
      ],
      "containers": [
        {"container": "hand_right", "owner": "CHAR_BOSS", "location": "hand_right", "contents": ["master_key"]}
      ],
      "invariants": [
        "inventory: CHAR_BOSS -> master_key (status=carried, container=hand_right)",
        "container: hand_right (owner=CHAR_BOSS, contents=[master_key])"
      ]
    }
  ]
}

