      "pronouns": "he/him",
      "role": "npc",
      "persona": {
        "core_traits": ["stressed", "greedy", "pompous", "cowardly"],
        "motivations": ["keeping his job", "avoiding taxes", "keeping the peace"],
        "fears": ["monster raids", "the System Administrator", "losing his gold"],
        "values": ["stability", "his own skin", "appearances"],
        "voice_notes": ["wheezy", "formal but shaky", "constantly clearing throat"],
        "arc_hint": "A minor hurdle who inadvertently provides Artie with his first real quest."
