      "inventory": [
        {"item": "City Ledger", "status": "stowed", "container": "hand_left"},
        {"item": "Key to Oakhaven", "status": "stowed", "container": "belt_pouch"}
      ],
      "containers": [
        {"container": "belt_pouch", "owner": "CHAR_MAYOR", "location": "waist", "contents": ["Key to Oakhaven"]}
      ],
      "invariants": [
        "inventory: CHAR_MAYOR -> City Ledger (status=stowed, container=hand_left)",
        "container: belt_pouch (owner=CHAR_MAYOR, contents=[Key to Oakhaven])"
      ]
    },
    {
      "character_id": "CHAR_BOSS",
      "name": "The System Administrator",
      "pronouns": "it/its",
      "role": "overarching villain",
      "persona": {
        "core_traits": ["cold", "logical", "perfectionist", "detached"],
        "motivations": ["maintaining system integrity", "eliminating bugs", "optimizing the world"],
        "fears": ["unfixable errors", "entropy", "the 'Delete' key"],
        "values": ["efficiency", "syntax", "balance"],
        "voice_notes": ["monotone", "echoing", "speaks in error codes"],
        "arc_hint": "The ultimate force of order that Artie must eventually 'hack' to survive."
