        "titles": [
          {"name": "The Architect", "source": "creation", "active": true}
        ]
      },
      "inventory": [
        {"item": "The Source Code", "status": "carried", "container": "hand_right"}
      ],
      "containers": [],
      "invariants": [
        "inventory: CHAR_BOSS -> The Source Code (status=carried, container=hand_right)"
      ]
    }
