{
  "schema_version": "1.0",
  "characters": [
    {
      "character_id": "CHAR_ARTIE",
      "name": "Arthur 'Artie' Penhaligon",
      "pronouns": "he/him",
      "role": "protagonist",
      "persona": {
        "core_traits": ["Snarky", "Genre-savvy", "Risk-averse (initially)", "Observant"],
        "motivations": ["Surviving the system", "Finding an exploit to get home", "Not getting hit once"],
        "fears": ["Taking damage", "Permanent deletion", "Corporate bureaucracy"],
        "values": ["Efficiency", "Fairness (ironically)", "Self-preservation"],
        "voice_notes": ["Dry wit", "Internal monologues about game mechanics", "Frequent use of 'Seriously?'"],
        "arc_hint": "From a man who avoids all risk to a glass-cannon legend who gambles everything on a single hit."
      },
      "character_continuity_system_state": {
        "stats": {
          "hp": {"current": 1, "max": 1},
          "mp": {"current": 10, "max": 10},
          "defense": {"current": 0, "max": 0},
          "luck": {"current": 999, "max": 999},
          "crit_chance": {"current": 100, "max": 100}
        },
        "skills": {
          "analyze_vending_machine": 1
        },
        "titles": [
          {"name": "The Glitched One", "source": "system_error", "active": true},
          {"name": "Glass Cannon", "source": "stat_distribution", "active": true}
        ],
        "resources": {
          "patience": {"current": 2, "max": 100}
        }
      },
      "inventory": [
        {"item_id": "ITEM_QUARTER", "item_name": "Sticky Quarter", "status": "carried", "container": "hand_right"},
        {"item_id": "ITEM_ID_BADGE", "item_name": "Corporate ID Badge", "status": "stowed", "container": "pocket_left"}
      ],
      "containers": [
        {"container": "pocket_left", "owner": "CHAR_ARTIE", "location": "pants", "contents": ["ITEM_ID_BADGE"]},
        {"container": "hand_right", "owner": "CHAR_ARTIE", "location": "arm_right", "contents": ["ITEM_QUARTER"]}
      ],
      "invariants": [
        "inventory: CHAR_ARTIE -> Sticky Quarter (status=carried, container=hand_right)",
        "inventory: CHAR_ARTIE -> Corporate ID Badge (status=stowed, container=pocket_left)",
        "container: pocket_left (owner=CHAR_ARTIE, contents=[Corporate ID Badge])",
        "injury: CHAR_ARTIE none"
      ]
    },
    {
      "character_id": "CHAR_FIZZ",
      "name": "Fizz",
      "pronouns": "she/her",
      "role": "companion/guide",
      "persona": {
        "core_traits": ["Hyperactive", "Bureaucratic", "Slightly sadistic", "Loyal"],
        "motivations": ["Meeting her guide-quota", "Watching Artie struggle", "Upgrading her own wings"],
        "fears": ["System audits", "Low ratings", "Silence"],
        "values": ["The Manual", "Efficiency", "Flashy visual effects"],
        "voice_notes": ["High-pitched", "Uses corporate jargon", "Fast talker"],
        "arc_hint": "Moves from a mindless system drone to a co-conspirator in Artie's exploits."
      },
      "character_continuity_system_state": {
        "stats": {
          "hp": {"current": 50, "max": 50},
          "speed": {"current": 100, "max": 100}
        },
        "skills": {
          "system_navigation": 10,
          "appraisal": 8,
          "annoying_buzz": 5
        },
        "titles": [
          {"name": "Level 1 Guide", "source": "employment", "active": true}
        ]
      },
      "inventory": [
        {"item_id": "ITEM_GUIDE_BOOK", "item_name": "The System Manual", "status": "stowed", "container": "satchel"}
      ],
      "containers": [
        {"container": "satchel", "owner": "CHAR_FIZZ", "location": "back", "contents": ["ITEM_GUIDE_BOOK"]}
      ],
      "invariants": [
        "inventory: CHAR_FIZZ -> The System Manual (status=stowed, container=satchel)",
        "container: satchel (owner=CHAR_FIZZ, contents=[The System Manual])"
      ]
    },
    {
      "character_id": "CHAR_VANE",
      "name": "Vane",
      "pronouns": "he/him",
      "role": "antagonist",
      "persona": {
        "core_traits": ["Arrogant", "Power-hungry", "Meticulous", "Cruel"],
        "motivations": ["Dominating the leaderboard", "Eliminating 'anomalies'", "Total control"],
        "fears": ["Irrelevance", "Being outsmarted by a 'noob'", "Losing his rank"],
        "values": ["Strength", "Hierarchy", "Optimization"],
        "voice_notes": ["Smooth", "Condescending", "Measured"],
        "arc_hint": "The 'perfect' player who is slowly unraveled by Artie's chaotic existence."
      },
      "character_continuity_system_state": {
        "stats": {
          "level": 50,
          "strength": 150,
          "defense": 120
        },
        "skills": {
          "heavy_strike": 15,
          "intimidate": 10
        },
        "titles": [
          {"name": "The Relentless", "source": "achievement", "active": true}
        ]
      },
      "inventory": [
        {"item_id": "ITEM_EBONY_BLADE", "item_name": "Ebony Greatsword", "status": "stowed", "container": "back_sheath"}
      ],
      "containers": [
        {"container": "back_sheath", "owner": "CHAR_VANE", "location": "back", "contents": ["ITEM_EBONY_BLADE"]}
      ],
      "invariants": [
        "inventory: CHAR_VANE -> Ebony Greatsword (status=stowed, container=back_sheath)"
      ]
    },
    {
      "character_id": "CHAR_MAYOR",
      "name": "Mayor of Oakhaven",
      "pronouns": "he/him",
      "role": "npc",
      "persona": {
        "core_traits": ["Pompous", "Cowardly", "Greedy", "Forgetful"],
        "motivations": ["Staying in power", "Avoiding monster attacks", "Collecting taxes"],
        "fears": ["Losing his office", "Direct combat", "The System Administrator"],
        "values": ["Tradition", "Gold", "His own safety"],
        "voice_notes": ["Wheezy", "Self-important", "Prone to long-winded speeches"],
        "arc_hint": "A minor obstacle who becomes a recurring source of frustration for Artie."
      },
      "character_continuity_system_state": {
        "stats": {
          "level": 5,
          "charisma": 12
        },
        "titles": [
          {"name": "Mayor", "source": "position", "active": true}
        ]
      },
      "inventory": [
        {"item_id": "ITEM_OFFICE_KEY", "item_name": "Key to Oakhaven", "status": "stowed", "container": "pocket"}
      ],
      "containers": [
        {"container": "pocket", "owner": "CHAR_MAYOR", "location": "waist", "contents": ["ITEM_OFFICE_KEY"]}
      ],
      "invariants": [
        "inventory: CHAR_MAYOR -> Key to Oakhaven (status=stowed, container=pocket)"
      ]
    },
    {
      "character_id": "CHAR_BOSS",
      "name": "The System Administrator",
      "pronouns": "it/its",
      "role": "overarching villain",
      "persona": {
        "core_traits": ["Cold", "Logical", "Indifferent", "Omnipresent"],
        "motivations": ["Maintaining system stability", "Deleting bugs", "Optimizing the world"],
        "fears": ["Unfixable errors", "Entropy", "Artie's luck stat"],
        "values": ["Order", "Data integrity", "The Patch Notes"],
        "voice_notes": ["Monotone", "Echoing", "Synthesized"],
        "arc_hint": "The ultimate force of order that must eventually acknowledge Artie as a 'feature' rather than a 'bug'."
      },
      "character_continuity_system_state": {
        "stats": {
          "level": 999,
          "hp": "INF",
          "mp": "INF"
        },
        "skills": {
          "ban_hammer": 99,
          "reality_rewrite": 99
        }
      },
      "inventory": [],
      "containers": [],
      "invariants": [
        "injury: CHAR_BOSS none"
      ]
    }
  ]
}

