text:
{
  "schema_version": "spine_v1",
  "chapters": [
    {
      "chapter_id": 1,
      "title": "Critical Failure",
      "goal": "Survive the glitchy transmigration into the world of Aethelgard and secure the first 'broken' skill.",
      "chapter_role": "hook",
      "stakes_shift": "From a dying human in the real world to a low-level anomaly with a permanent 100% crit-rate glitch.",
      "bridge": {
        "from_prev": "The protagonist's final moments in a hospital bed.",
        "to_next": "Artie realizes his starting gear cannot handle his massive damage output and will shatter upon the next hit."
      },
      "pacing": {
        "intensity": 4,
        "tempo": "steady",
        "expected_scene_count": 6
      }
    },
    {
      "chapter_id": 2,
      "title": "The Loot Loop",
      "goal": "Exploit a repeatable tutorial quest to acquire 'Indestructible' tier starter gear.",
      "chapter_role": "setup",
      "stakes_shift": "From a fragile glass cannon to a geared-up exploiter capable of surviving basic System Sentinels.",
      "bridge": {
        "from_prev": "Artie's realization that his glitch breaks standard equipment.",
        "to_next": "The 'Tutorial Completion' trigger alerts the System to an illegal stat distribution."
      },
      "pacing": {
        "intensity": 3,
        "tempo": "steady",
        "expected_scene_count": 5
      }
    },
    {
      "chapter_id": 3,
      "title": "Aggro Magnet",
      "goal": "Escape the starting zone while being pursued by high-level 'Correction' mobs.",
      "chapter_role": "journey",
      "stakes_shift": "From a local anomaly to a regional fugitive with a rising 'System Threat' level.",
      "bridge": {
        "from_prev": "The System's automated detection of Artie's illegal gear.",
        "to_next": "Artie finds a hidden sanctuary used by other 'glitched' players."
      },
      "pacing": {
        "intensity": 4,
        "tempo": "rush",
        "expected_scene_count": 7
      }
    },
    {
      "chapter_id": 4,
      "title": "The Dev-Killers",
      "goal": "Investigate the 'Underworld Market' to find a way to mask his glitch from the System's radar.",
      "chapter_role": "investigation",
      "stakes_shift": "From a lone survivor to a potential member of a revolutionary group of exploiters.",
      "bridge": {
        "from_prev": "Arrival at the sanctuary of the Dev-Killers.",
        "to_next": "A betrayal within the sanctuary reveals Artie's location to the regional Governor."
      },
      "pacing": {
        "intensity": 3,
        "tempo": "slow_burn",
        "expected_scene_count": 6
      }
    },
    {
      "chapter_id": 5,
      "title": "Hyper-Inflation",
      "goal": "Execute a high-stakes currency exploit to fund the purchase of a 'Legendary' stealth cloak.",
      "chapter_role": "pressure",
      "stakes_shift": "From a mere fugitive to an economic terrorist destabilizing the game's core systems.",
      "bridge": {
        "from_prev": "The need for high-tier stealth to bypass the Governor's gates.",
        "to_next": "The economic collapse triggers an emergency 'World Event' boss spawn."
      },
      "pacing": {
        "intensity": 4,
        "tempo": "steady",
        "expected_scene_count": 8
      }
    },
    {
      "chapter_id": 6,
      "title": "The Hotfix",
      "goal": "Defeat the System's 'Correction Avatar' before the zone is hard-reset and Artie is deleted.",
      "chapter_role": "climax",
      "stakes_shift": "From a threat to the economy to a direct existential threat to the System's control.",
      "bridge": {
        "from_prev": "The arrival of the Correction Avatar in the middle of the market collapse.",
        "to_next": "The Avatar's defeat leaves a hole in the game's source code."
      },
      "pacing": {
        "intensity": 5,
        "tempo": "rush",
        "expected_scene_count": 10
      }
    },
    {
      "chapter_id": 7,
      "title": "Unpatched",
      "goal": "Escape through the code-tear into an 'Unmapped' zone to evade the global server reboot.",
      "chapter_role": "aftermath",
      "stakes_shift": "From a hunted glitch to a pioneer in the 'Wild Code' where the System has no power.",
      "bridge": {
        "from_prev": "The destruction of the Correction Avatar and the temporary suspension of System laws.",
        "to_next": "Artie enters the 'Beta Lands,' where his glitches are the only rules that matter."
      },
      "pacing": {
        "intensity": 2,
        "tempo": "steady",
        "expected_scene_count": 4
      }
    }
  ]
}

