text:
{
  "schema_version": "sections_v1",
  "chapters": [
    {
      "chapter_id": 1,
      "sections": [
        {
          "section_id": 1,
          "title": "The Final Breath",
          "intent": "Establish Artie's transition from a terminal patient to a confused player in a glitching world.",
          "section_role": "setup",
          "target_scene_count": 2,
          "end_condition": "Artie opens his eyes in the Aethelgard starting zone and sees the first UI glitch."
        },
        {
          "section_id": 2,
          "title": "The 100% Solution",
          "intent": "Introduce the core mechanic of the permanent 100% crit-rate glitch through combat.",
          "section_role": "rising_action",
          "target_scene_count": 2,
          "end_condition": "Artie kills a tutorial mob with a massive overkill critical hit."
        },
        {
          "section_id": 3,
          "title": "Glass Cannon Problems",
          "intent": "Reveal the downside of the glitch: the damage output destroys standard equipment.",
          "section_role": "rising_action",
          "target_scene_count": 2,
          "end_condition": "Artie's starting dagger shatters into dust after a final critical strike."
        }
      ]
    },
    {
      "chapter_id": 2,
      "sections": [
        {
          "section_id": 1,
          "title": "Finding the Loop",
          "intent": "Artie discovers a way to repeat the tutorial quest to farm specific rewards.",
          "section_role": "rising_action",
          "target_scene_count": 2,
          "end_condition": "Artie confirms the tutorial quest 'First Steps' has failed to flag as completed, allowing a restart."
        },
        {
          "section_id": 2,
          "title": "Gear Up",
          "intent": "Artie exploits the loop until he acquires gear that can withstand his damage.",
          "section_role": "development",
          "target_scene_count": 3,
          "end_condition": "Artie equips the 'Indestructible Ironwood Blade' and prepares to leave the tutorial."
        }
      ]
    },
    {
      "chapter_id": 3,
      "sections": [
        {
          "section_id": 1,
          "title": "System Ping",
          "intent": "The System's automated security detects the gear and stat anomaly.",
          "section_role": "rising_action",
          "target_scene_count": 2,
          "end_condition": "A System Sentinel manifests in the glade to investigate the anomaly."
        },
        {
          "section_id": 2,
          "title": "The Great Escape",
          "intent": "Artie must navigate the starting zone while avoiding or distracting the high-level Sentinels.",
          "section_role": "rising_action",
          "target_scene_count": 3,
          "end_condition": "Artie crosses the physical boundary of the tutorial zone into the open world."
        },
        {
          "section_id": 3,
          "title": "Into the Wilds",
          "intent": "Artie seeks a place where the System's direct oversight is weaker.",
          "section_role": "transition",
          "target_scene_count": 2,
          "end_condition": "Artie reaches the outskirts of the Grey Market, a known low-surveillance zone."
        }
      ]
    },
    {
      "chapter_id": 4,
      "sections": [
        {
          "section_id": 1,
          "title": "Shadow Economy",
          "intent": "Artie navigates the social and mechanical complexities of the underground market.",
          "section_role": "setup",
          "target_scene_count": 2,
          "end_condition": "Artie gains entry to the 'Null-Sector' where the most dangerous exploiters hide."
        },
        {
          "section_id": 2,
          "title": "The Dev-Killers",
          "intent": "Artie meets a group of players dedicated to breaking the game's rules.",
          "section_role": "development",
          "target_scene_count": 2,
          "end_condition": "Artie is accepted into the Dev-Killer hideout after demonstrating his glitch."
        },
        {
          "section_id": 3,
          "title": "The Masking Plan",
          "intent": "The group identifies a legendary item that can hide Artie's signature from the System.",
          "section_role": "rising_action",
          "target_scene_count": 2,
          "end_condition": "Artie identifies the Void-Stitch Cloak as his primary objective."
        }
      ]
    },
    {
      "chapter_id": 5,
      "sections": [
        {
          "section_id": 1,
          "title": "Market Manipulation",
          "intent": "Artie and the Dev-Killers use a currency glitch to crash the local economy.",
          "section_role": "rising_action",
          "target_scene_count": 3,
          "end_condition": "The auction house prices for essential goods begin to spiral into hyper-inflation."
        },
        {
          "section_id": 2,
          "title": "The Big Short",
          "intent": "Artie converts the chaos into enough gold to buy the legendary cloak.",
          "section_role": "rising_action",
          "target_scene_count": 3,
          "end_condition": "Artie collects the massive gold payout from the market collapse."
        },
        {
          "section_id": 3,
          "title": "Buying Silence",
          "intent": "Artie acquires the cloak just as the System's 'World Event' response triggers.",
          "section_role": "development",
          "target_scene_count": 2,
          "end_condition": "Artie equips the Void-Stitch Cloak and his System Threat level vanishes."
        }
      ]
    },
    {
      "chapter_id": 6,
      "sections": [
        {
          "section_id": 1,
          "title": "The Correction",
          "intent": "The System sends its ultimate enforcement unit to delete the glitched area.",
          "section_role": "rising_action",
          "target_scene_count": 3,
          "end_condition": "The Correction Avatar descends from the sky and initiates the zone reset."
        },
        {
          "section_id": 2,
          "title": "Breaking the Boss",
          "intent": "Artie uses his 100% crit rate and indestructible gear to fight a being of pure code.",
          "section_role": "climax",
          "target_scene_count": 4,
          "end_condition": "Artie lands a final, glitch-boosted critical hit on the Avatar's core."
        },
        {
          "section_id": 3,
          "title": "Code Collapse",
          "intent": "The defeat of the Avatar causes the local reality to begin unraveling.",
          "section_role": "falling_action",
          "target_scene_count": 3,
          "end_condition": "The Correction Avatar dissolves into raw code fragments, leaving a hole in the world."
        }
      ]
    },
    {
      "chapter_id": 7,
      "sections": [
        {
          "section_id": 1,
          "title": "The Tear in the World",
          "intent": "Artie must find the exit before the zone is hard-reset and he is deleted.",
          "section_role": "falling_action",
          "target_scene_count": 2,
          "end_condition": "Artie locates the code-tear in the center of the collapsing Grey Market."
        },
        {
          "section_id": 2,
          "title": "Beyond the Patch",
          "intent": "Artie escapes the known world for the unmapped Beta Lands.",
          "section_role": "resolution",
          "target_scene_count": 2,
          "end_condition": "Artie steps through the tear into the Beta Lands as the server reboot begins."
        }
      ]
    }
  ]
}

