  "device_presence": ["DEVICE_anomaly_tag"],
  "transition_type": "travel_arrival",
  "timeline_scope": "present",
  "ontological_scope": "real",
  "continuity_system_focus": ["stats", "resources"],
  "ui_mechanics_expected": ["HP", "Stamina"]
}

