  "schema_version": "1.0",
  "summary_update": {
    "chapter_so_far_add": [
      "Artie survived his first combat encounter by vaporizing a slime and an oak tree with a massive critical hit.",
      "Artie reached Level 2, discovering that his damage output is world-breaking while his survivability remains non-existent."
    ],
    "key_events": [
      "Artie missed the slime exactly five times, confirming the mechanical difficulty of his 1 HP build.",
      "Artie landed a 'SUPER-MEGA-ULTRA-CRIT!' on his sixth attempt, dealing 9,999,999 damage.",
      "The impact vaporized the slime and completely destroyed a massive oak tree, leaving a scorched crater.",
      "The System issued a warning regarding environmental destruction and the janitorial sub-routines.",
      "Artie reached Level 2 and gained 5 unallocated stat points.",
      "Artie confirmed his HP remains at 1/1 despite the level up."
    ],
    "last_scene": [
      "Artie was cornered by a Level 1 Lesser Forest Slime against an oak tree in the Tutorial Zone.",
      "After missing five swings due to poor hit detection, he landed a 'SUPER-MEGA-ULTRA-CRIT!' that vaporized the monster and the tree.",
      "The resulting explosion left a scorched crater and propelled Artie to Level 2."
    ],
    "must_stay_true": [
      "milestone: first_kill = DONE",
      "status: CHAR_ARTIE -> 1/1 HP (hard constraint)",
      "status: CHAR_ARTIE -> 100% Critical Hit Rate (hard constraint)",
      "status: CHAR_ARTIE -> Level 2",
