  "schema_version": "1.0",
  "summary_update": {
    "last_scene": [
      "Artie faced a Level 1 Lesser Forest Slime while cornered against a massive oak tree in the Tutorial Zone.",
      "After missing five consecutive swings, Artie landed a 'SUPER-MEGA-ULTRA-CRIT' that vaporized the slime and the tree instantly.",
      "The resulting explosion left a twenty-foot wide crater and propelled Artie to Level 2, though he remains at 1 HP."
    ],
    "key_events": [
      "Artie missed five consecutive attacks against a Level 1 slime due to erratic hit detection.",
      "Artie realized his 100% Crit Rate is a system law that eventually forces a hit regardless of 'Prop' weapon status.",
      "Artie landed a 'SUPER-MEGA-ULTRA-CRIT' dealing 9,999,999 damage to a 10 HP enemy.",
      "The impact vaporized the slime and completely destroyed a massive oak tree, leaving a glassed crater.",
      "Artie reached Level 2 and gained 5 unallocated stat points.",
      "The System issued a formal warning regarding environmental destruction and 'Ash-Related' janitorial tickets."
    ],
    "must_stay_true": [
      "milestone: first_kill = DONE",
      "milestone: level_2_reached = DONE",
      "status: CHAR_ARTIE -> 1/1 HP (hard constraint)",
      "status: CHAR_ARTIE -> 100% Critical Hit Rate",
      "inventory: CHAR_ARTIE -> Rusty Shortsword (Prop) (carried, hand_right)",
      "location: Tutorial Zone (Crater Site)"
    ],
    "chapter_so_far_add": [
      "Artie survived his first combat encounter by accidentally triggering a catastrophic environmental glitch.",
      "The 'Prop' sword has been established as a conduit for massive overkill damage due to Artie's broken stats.",
      "Artie reached Level 2, confirming that his progression is tied to high-magnitude 'System Overload' events."
    ]
  },
  "character_updates": [
    {
      "character_id": "CHAR_ARTIE",
      "scene": 4,
      "chapter": 1,
      "persona_updates": [
        "Artie now views his 'Prop' sword as a high-yield detonator rather than a conventional weapon.",
        "He is developing a cynical understanding of the System's mathematical inevitability and its disregard for collateral damage."
      ],
      "invariants_add": [
