    {
      "character_id": "CHAR_VANE",
      "chapter": 1,
      "scene": 8,
      "persona_updates": [
        "Vane is suspicious of Artie's 'Anomaly' status and views him as a potential System virus that needs patching.",
        "Vane values System balance and data over gold, acting as a self-appointed enforcer of the Order of the Golden Ratio."
      ],
      "invariants_add": [
        "milestone: vane_first_encounter = DONE"
      ],
      "inventory": [
        {
          "container": "sheath",
          "item": "Masterwork Rapier",
          "status": "stowed"
        },
