{
  "schema_version": "1.0",
  "characters": [
    {
      "character_id": "CHAR_kaelen",
      "name": "Kaelen",
      "pronouns": "he/him",
      "role": "protagonist",
      "persona": {
        "core_traits": [
          "stoic",
          "resilient",
          "observant"
        ],
        "motivations": [
          "protecting his village",
          "uncovering his lineage"
        ],
        "fears": [
          "loss of autonomy",
          "failing those he loves"
        ],
        "values": [
          "loyalty",
          "hard work",
          "honesty"
        ],
        "voice_notes": [
          "direct",
          "sparse",
          "grounded"
        ],
        "arc_hint": "From village laborer to star-wrought champion."
      },
      "inventory": [
        {
          "item": "iron-bound staff",
          "status": "carried",
          "container": "hand_right"
        },
        {
          "item": "leather pouch",
          "status": "equipped",
          "container": "belt"
        }
      ],
      "containers": [
        {
          "container": "leather pouch",
          "owner": "CHAR_kaelen",
          "location": "belt",
          "contents": [
            "dried meat",
            "flint",
            "whetstone"
          ]
        }
      ],
      "invariants": [
        "inventory: CHAR_kaelen -> iron-bound staff (status=carried, container=hand_right)",
        "inventory: CHAR_kaelen -> leather pouch (status=equipped, container=belt)",
        "container: leather pouch (owner=CHAR_kaelen, contents=[dried meat, flint, whetstone])"
      ]
    },
    {
      "character_id": "CHAR_lyra",
      "name": "Lyra",
      "pronouns": "she/her",
      "role": "protagonist",
      "persona": {
        "core_traits": [
          "intellectual",
          "restless",
          "witty"
        ],
        "motivations": [
          "seeking forbidden knowledge",
          "escaping social constraints"
        ],
        "fears": [
          "stagnation",
          "being forgotten"
        ],
        "values": [
          "truth",
          "discovery",
          "independence"
        ],
        "voice_notes": [
          "precise",
          "inquisitive",
          "occasionally sarcastic"
        ],
        "arc_hint": "Learning that knowledge without action is hollow."
      },
      "inventory": [
        {
          "item": "star-chart",
          "status": "carried",
          "container": "hand_left"
        },
        {
          "item": "satchel",
          "status": "equipped",
          "container": "shoulder_right"
        }
      ],
      "containers": [
        {
          "container": "satchel",
          "owner": "CHAR_lyra",
          "location": "shoulder_right",
          "contents": [
            "inkwell",
            "quill",
            "blank parchment"
          ]
        }
      ],
      "invariants": [
        "inventory: CHAR_lyra -> star-chart (status=carried, container=hand_left)",
        "inventory: CHAR_lyra -> satchel (status=equipped, container=shoulder_right)",
        "container: satchel (owner=CHAR_lyra, contents=[inkwell, quill, blank parchment])"
      ]
    },
    {
      "character_id": "CHAR_vane",
      "name": "Vane",
      "pronouns": "he/him",
      "role": "antagonist",
      "persona": {
        "core_traits": [
          "calculating",
          "charismatic",
          "cold"
        ],
        "motivations": [
          "seizing absolute power",
          "rewriting cosmic order"
        ],
        "fears": [
          "chaos",
          "irrelevance"
        ],
        "values": [
          "order",
          "strength",
          "legacy"
        ],
        "voice_notes": [
          "measured",
          "authoritative",
          "condescending"
        ],
        "arc_hint": "A descent into obsession that blinds him to his own destruction."
      },
      "inventory": [
        {
          "item": "obsidian dagger",
          "status": "stowed",
          "container": "sheath"
        },
        {
          "item": "signet ring",
          "status": "equipped",
          "container": "finger_right"
        }
      ],
      "containers": [
        {
          "container": "sheath",
          "owner": "CHAR_vane",
          "location": "belt_left",
          "contents": [
            "obsidian dagger"
          ]
        }
      ],
      "invariants": [
        "inventory: CHAR_vane -> obsidian dagger (status=stowed, container=sheath)",
        "inventory: CHAR_vane -> signet ring (status=equipped, container=finger_right)",
        "container: sheath (owner=CHAR_vane, contents=[obsidian dagger])"
      ]
    },
    {
      "character_id": "CHAR_astra",
      "name": "Astra",
      "pronouns": "she/they",
      "role": "mentor/entity",
      "persona": {
        "core_traits": [
          "ethereal",
          "patient",
          "cryptic"
        ],
        "motivations": [
          "restoring cosmic balance",
          "fulfilling the oath"
        ],
        "fears": [
          "the final darkness",
          "breaking of the cycle"
        ],
        "values": [
          "continuity",
          "harmony",
          "sacrifice"
        ],
        "voice_notes": [
          "echoing",
          "metaphorical",
          "detached"
        ],
        "arc_hint": "Transitioning from a distant observer to a catalyst for change."
      },
      "inventory": [
        {
          "item": "silver locket",
          "status": "equipped",
          "container": "neck"
        },
        {
          "item": "velvet pouch",
          "status": "equipped",
          "container": "belt"
        }
      ],
      "containers": [
        {
          "container": "velvet pouch",
          "owner": "CHAR_astra",
          "location": "belt",
          "contents": [
            "starlight dust"
          ]
        }
      ],
      "invariants": [
        "inventory: CHAR_astra -> silver locket (status=equipped, container=neck)",
        "inventory: CHAR_astra -> velvet pouch (status=equipped, container=belt)",
        "container: velvet pouch (owner=CHAR_astra, contents=[starlight dust])"
      ]
    }
  ]
}

