  "schema_version": "1.0",
  "characters": [
    {
      "character_id": "CHAR_kaelen",
      "name": "Kaelen",
      "pronouns": "he/him",
      "role": "protagonist",
      "persona": {
        "core_traits": ["resilient", "taciturn", "mechanically minded"],
        "motivations": ["protecting his village", "uncovering the source of the star-fall"],
        "fears": ["helplessness", "the corruption of the land"],
        "values": ["loyalty", "hard work", "directness"],
        "voice_notes": ["short, punchy sentences", "focuses on physical sensations", "avoids flowery metaphors"],
        "arc_hint": "From a simple laborer to the binder of the Starwrought Oath."
      },
      "inventory": [
        {"item": "star-iron_shard", "status": "carried", "container": "hand_right"},
        {"item": "whetstone", "status": "stowed", "container": "belt_pouch"},
        {"item": "leather_boots", "status": "equipped", "container": "feet"}
      ],
      "containers": [
        {"container": "belt_pouch", "owner": "CHAR_kaelen", "location": "waist_right", "contents": ["whetstone"]}
      ],
      "invariants": [
        "inventory: CHAR_kaelen -> star-iron_shard (status=carried, container=hand_right)",
        "inventory: CHAR_kaelen -> whetstone (status=stowed, container=belt_pouch)",
        "container: belt_pouch (owner=CHAR_kaelen, contents=[whetstone])"
      ]
    },
    {
      "character_id": "CHAR_lyra",
      "name": "Lyra",
      "pronouns": "she/her",
      "role": "protagonist",
      "persona": {
        "core_traits": ["observant", "erudite", "cautious"],
        "motivations": ["preserving lost knowledge", "finding her missing mentor"],
        "fears": ["intellectual stagnation", "the loss of history"],
        "values": ["truth", "curiosity", "preservation"],
        "voice_notes": ["precise vocabulary", "analytical internal monologue", "frequent use of 'perhaps'"],
        "arc_hint": "Learning that knowledge is a weapon that must be wielded, not just stored."
      },
      "inventory": [
        {"item": "charcoal_stick", "status": "carried", "container": "hand_left"},
        {"item": "ink-stained_journal", "status": "stowed", "container": "satchel"},
        {"item": "silver_locket", "status": "equipped", "container": "neck"}
      ],
      "containers": [
        {"container": "satchel", "owner": "CHAR_lyra", "location": "shoulder_left", "contents": ["ink-stained_journal"]}
      ],
      "invariants": [
        "inventory: CHAR_lyra -> charcoal_stick (status=carried, container=hand_left)",
        "inventory: CHAR_lyra -> ink-stained_journal (status=stowed, container=satchel)",
        "container: satchel (owner=CHAR_lyra, contents=[ink-stained_journal])"
      ]
    },
    {
      "character_id": "CHAR_vane",
      "name": "Vane",
      "pronouns": "he/him",
      "role": "antagonist",
      "persona": {
        "core_traits": ["calculating", "ruthless", "disciplined"],
        "motivations": ["restoring family legacy", "harnessing starwrought power"],
        "fears": ["obscurity", "the collapse of order"],
        "values": ["hierarchy", "efficiency", "legacy"],
        "voice_notes": ["formal", "authoritative", "often uses commands"],
        "arc_hint": "A descent into obsession that blinds him to the true nature of the oath."
      },
      "inventory": [
        {"item": "black_steel_rapier", "status": "stowed", "container": "sheath"},
        {"item": "signet_ring", "status": "equipped", "container": "finger_right"},
        {"item": "sealed_scroll", "status": "stowed", "container": "inner_pocket"}
      ],
      "containers": [
        {"container": "sheath", "owner": "CHAR_vane", "location": "waist_left", "contents": ["black_steel_rapier"]},
        {"container": "inner_pocket", "owner": "CHAR_vane", "location": "tunic_chest", "contents": ["sealed_scroll"]}
      ],
      "invariants": [
        "inventory: CHAR_vane -> black_steel_rapier (status=stowed, container=sheath)",
        "inventory: CHAR_vane -> sealed_scroll (status=stowed, container=inner_pocket)",
        "container: sheath (owner=CHAR_vane, contents=[black_steel_rapier])",
        "container: inner_pocket (owner=CHAR_vane, contents=[sealed_scroll])"
      ]
    },
    {
      "character_id": "CHAR_astra",
      "name": "Astra",
      "pronouns": "she/they",
      "role": "mentor/entity",
      "persona": {
        "core_traits": ["ethereal", "cryptic", "ancient"],
        "motivations": ["maintaining cosmic balance", "guiding the chosen"],
        "fears": ["the extinguishing of the first flame", "entropy"],
        "values": ["balance", "destiny", "sacrifice"],
        "voice_notes": ["metaphorical", "echoing tone", "speaks in riddles"],
        "arc_hint": "Moving from a distant observer to a direct participant in the mortal realm."
      },
      "inventory": [
        {"item": "glass_orb", "status": "carried", "container": "hand_right"}
      ],
      "containers": [
        {"container": "hand_right", "owner": "CHAR_astra", "location": "hand_right", "contents": ["glass_orb"]}
      ],
      "invariants": [
        "inventory: CHAR_astra -> glass_orb (status=carried, container=hand_right)",
        "container: hand_right (owner=CHAR_astra, contents=[glass_orb])"
      ]
    }
  ]
}

